import React from "react";
import { Link, useState } from "react-router-dom";
import "./DrinkMenu.css";
import {
    Card,
    CardBody,
    CardTitle,
    CardText,
    ListGroup,
    ListGroupItem
} from "reactstrap";
import AddDrinkForm from "./AddDrinkForm";

function DrinkMenu() {
    const[drinks, setDrinks] = useState([]);

    const renderDrinks = () =>{
    return (
        <section className="col-md-4">
            <Card>
                <CardBody>
                    <CardTitle className="font-weight-bold text-center">
                        Drink Menu
                    </CardTitle>
                    <CardText>
                        Thirsty? Today's drink menu has an option for everyone.
                    </CardText>
                    <ListGroup>
                        {drinks.map(drink => (
                            <Link to={`/drinks/${drink.id}`} key={drink.id}>
                                <ListGroupItem>{drink.name}</ListGroupItem>
                             </Link>
                        ))}
                    </ListGroup>
                </CardBody>
            </Card>
        </section>
    );
}
    const createDrink = drink => {
        let newDrink = {...drink, id: "name"};
        setDrinks(drinks => [...drinks, newDrink]);
    };
    return(
        <div className="DrinkMenu">
            <AddDrinkForm createDrink={createDrink} />
            {renderDrinks()}
        </div>
    );
};

export default DrinkMenu;
