import React, { useState } from "react";
// import "./MadlibForm.css";

function AddDrinkForm({ createDrink }) {
    const [drink, setDrink] = useState({
        name: "",
        description: "",
        recipe: "",
        serve: ""
    });

    const handleChange = evt => {
        const { name, value } = evt.target;
        setDrink(drink => ({
            ...drink,
            [name]: value
        }));
    };


    const gatherInput = evt => {
        evt.preventDefault();
        createDrink({ ...drink, id: "name" });
        setDrink({name: "", description: "", recipe: "", serve: ""});
    };

    return (
        <div>
            <form className="fill" onSubmit={gatherInput}>
                <div>
                    <label htmlFor="name">Name</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        name="name"
                        value={drink.name}
                        id="name"
                    />
                </div>
                <div>
                    <label htmlFor="description">Description</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        name="description"
                        value={drink.description}
                        id="description"
                    />
                </div>
                <div>
                    <label htmlFor="recipe">Recipe</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        name="recipe"
                        value={drink.recipe}
                        id="recipe"
                    />
                </div>
                <div>
                    <label htmlFor="serve">Serve</label>
                    <input
                        onChange={handleChange}
                        type="text"
                        name="serve"
                        value={drink.serve}
                        id="serve"
                    />
                </div>
                <button id="newLibButton">Add a Drink</button>
            </form>
        </div>
    );
}
export default AddDrinkForm;
